import { useState } from 'react';
import styles from './AddTransaction.module.css'

const AddTransaction = (props) => {
    const [isVisible, setIsVisible] = useState(false)

    const [transactionTitle, settransactionTitle] = useState('')
    const [transactionAmount, settransactionAmount] = useState('')
    const [transactionType, settransactionType] = useState('')

    const toggleVisibility = () => {
        setIsVisible(!isVisible)
    }

    const addExpense = (e) => {
        e.preventDefault()
        if(transactionTitle.value.trim() && transactionAmount.value.trim() && transactionType.value) {
            console.log(transactionTitle.value)
        }
    }

    const getExpenseType = (e) => {
        console.log(e.target.value)
    }

    return (
        <div>
            <a onClick={toggleVisibility} className={`${styles.button} ${!isVisible ? styles['button--add'] : styles['button--cancel']}`}>{!isVisible ? 'Add transaction' : 'Cancel'}</a>

            {isVisible && <form action="" className={styles['transaction-form']}>
                <label htmlFor="description">Transaction Title</label>
                <input id="description" type="text" value={expenseTitle} />
                <label htmlFor="amount">Transaction Amount</label>
                <input id="amount" type="number" value={expenseAmount} />
                <p>Transaction Type:</p>
                <div onChange={getExpenseType}>
                    <div>
                        <input type="radio" id="income" name="transaction-type" value="income" /><label htmlFor="income">Income</label>
                    </div>
                    <div>
                        <input type="radio" id="expense" name="transaction-type" value="expense" /><label htmlFor="expense">Expense</label>
                    </div>
                </div>
                <button type="submit" className={`${styles.button} ${styles['button--add']}`} onClick={addExpense}>Add Transaction</button>
            </form>}
        </div>
    )
}

export default AddTransaction
