import {useState} from 'react'

import styles from './Header.module.css'

const Header = (props) => {
    const [totalIncome, setTotalIncome] = useState(0);
    const [totalExpense, setTotalExpense] = useState(0);

    let newIncome = 0
    let newExpense = 0

    for (const transaction in props.transactions) {
        if(transaction.type === "income") {
            newIncome += transaction.amount
        } else {
            newExpense += transaction.amount
        }
    }

    console.log(newIncome)

    return (
        <div className={styles.header}>
            <h1 className={styles.title}>Expense Tracker</h1>
            <div className={styles.balance}>
                <p className={styles["balance__text"]}>Your Balance</p>
                <p className={styles["balance__amount"]}>{ totalIncome - totalExpense}</p>
            </div>
            <div className={styles["transactions-summary"]}>
                <div className={styles["transactions-summary__column"]}>
                    <p className={styles["transactions-summary__title"]}>Income</p>
                    <p className={`${styles["transactions-summary__amount"]} ${styles.income}`}>${totalIncome}</p>
                </div>
                <div className={styles["transactions-summary__separator"]}/>
                <div className={styles["transactions-summary__column"]}>
                    <p className={styles["transactions-summary__title"]}>Expense</p>
                    <p className={`${styles["transactions-summary__amount"]} ${styles.expense}`}>${totalExpense}</p>
                </div>
            </div>
        </div>
    )
}

export default Header
