import { useState } from 'react';
import styles from './AddTransaction.module.css'

const AddTransaction = (props) => {
    const [isVisible, setIsVisible] = useState(false)

    const toggleVisibility = () => {
        setIsVisible(!isVisible)
    }

    const addExpense = (e) => {
        e.preventDefault()
        console.log('clicked')
    } 
    return (
        <div>
            <a onClick={toggleVisibility} className={`${styles.button} ${!isVisible ? styles['button--add'] : styles['button--cancel']}`}>{!isVisible ? 'Add transaction' : 'Cancel'}</a>

            {isVisible && <form action="" className={styles['transaction-form']}>
                <label htmlFor="description">Transaction Title</label>
                <input id="description" type="text" />
                <label htmlFor="amount">Transaction Amount</label>
                <input id="amount" type="number" />
                <p>Transaction Type:</p>
                <input id="radio" value="income" name="transaction-type" /><label htmlFor="type" id="income">Transaction Amount</label>
                <input id="radio" value="expense" name="transaction-type" /><label htmlFor="type" id="expense">Transaction Amount</label>
                <button type="submit" className={`${styles.button} ${styles['button--add']}`} onClick={addExpense}>Add Transaction</button>
            </form>}
        </div>
    )
}

export default AddTransaction
